$(function(e) {
    e("html").removeClass("no-js"),
    e("header a").click(function(t) {
        if (!e(this).hasClass("no-scroll")) {
            t.preventDefault();
            var n = e(this).attr("href")
              , a = e(n).offset().top;
            e("html, body").animate({
                scrollTop: a + "px"
            }, Math.abs(window.pageYOffset - e(n).offset().top) / 4),
            e("header").hasClass("active") && e("header, body").removeClass("active")
        }
    }),
    e("#to-top").click(function() {
        e("html, body").animate({
            scrollTop: 0
        }, 500)
    }),
    e("#lead-down span").click(function() {
        var t = e("#lead").next().offset().top;
        e("html, body").animate({
            scrollTop: t + "px"
        }, 500)
    }),
    e("#experience-timeline").each(function() {
        $this = e(this),
        $userContent = $this.children("div"),
        $userContent.each(function() {
            e(this).addClass("vtimeline-content").wrap('<div class="vtimeline-point"><div class="vtimeline-block"></div></div>')
        }),
        $this.find(".vtimeline-point").each(function() {
            e(this).prepend('<div class="vtimeline-icon"><i class="fas fa-map-marker-alt"></i></div>')
        }),
        $this.find(".vtimeline-content").each(function() {
            var t = e(this).data("date");
            t && e(this).parent().prepend('<span class="vtimeline-date">' + t + "</span>")
        })
    }),
    e("#mobile-menu-open").click(function() {
        e("header, body").addClass("active")
    }),
    e("#mobile-menu-close").click(function() {
        e("header, body").removeClass("active")
    }),
    e("#view-more-projects").click(function(t) {
        t.preventDefault(),
        e(this).fadeOut(300, function() {
            e("#more-projects").fadeIn(300)
        })
    })
}),
$(document).ready(function() {
    $(".acknowledgements-wrapper").bind("mousewheel DOMMouseScroll", function(e) {
        var t = null;
        "mousewheel" == e.type ? t = -1 * e.originalEvent.wheelDelta : "DOMMouseScroll" == e.type && (t = 40 * e.originalEvent.detail),
        t && (e.preventDefault(),
        $(this).scrollTop(t + $(this).scrollTop()))
    }),
    $(".acknowledgements-link").click(function() {
        $(".acknowledgements-wrapper").addClass("show"),
        $(".acknowledgements-overlay").addClass("show"),
        $(".acknowledgements-exit").addClass("show")
    }),
    $(".acknowledgements-overlay, .acknowledgements-exit").click(function() {
        $(".acknowledgements-wrapper").removeClass("show"),
        $(".acknowledgements-overlay").removeClass("show"),
        $(".acknowledgements-exit").removeClass("show")
    })
});
